export type ProgramKind = keyof typeof COURSE_CODE_SETS;
export type ProgramName<K extends ProgramKind> = keyof (typeof COURSE_CODE_SETS)[K];

// Helper: find which programs include a course code
export function findProgramsByCourseCode(courseCode: string) {
  const hits: Array<{ kind: ProgramKind; name: string }> = [];
  (Object.keys(COURSE_CODE_SETS) as ProgramKind[]).forEach((kind) => {
    const programs = COURSE_CODE_SETS[kind] as Record<string, readonly string[]>;
    Object.entries(programs).forEach(([name, codes]) => {
      if (codes.includes(courseCode)) hits.push({ kind, name });
    });
  });
  return hits;
}

/**
 * Standardized Major Codes
 * Used for mapping user input (which might be a code like 'EC' or 'CS')
 * to the exact major name keys in COURSE_CODE_SETS.
 */
export enum MajorCode {
  MC = 'MC', // 기계로봇
  MA = 'MA', // 신소재
  EV = 'EV', // 환경에너지
  BS = 'BS', // 생명과학
  BE = 'FE', // 의생명 (Using FE as code base, usually Biomedical)
  PS = 'PS', // 물리광과학
  MM = 'MM', // 수리과학
  CH = 'CH', // 화학
  AI = 'AI', // AI융합
  SE = 'SE', // 반도체
  EC = 'EC', // 전기전자컴퓨터
}

